import React from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";
import avatar from "../assets/profile_placeholder.png"; // Assurez-vous de mettre le bon chemin vers votre image
import 'bootstrap/dist/css/bootstrap.min.css';

const Navbar = () => {
  const isLoggedIn = false; // Remplacez ceci par votre logique d'authentification

  const handleLoginLogout = () => {
    console.log(isLoggedIn ? "Logout" : "Login");
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          <h1 className="text-center display-4 my-4">Book CLUB</h1>
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse justify-content-between" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/">Accueil</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/register">Register</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/books/new">New</Link>
            </li>
          </ul>
          <div className="navbar-nav ml-auto">
            <button className="nav-link btn btn-link" onClick={handleLoginLogout}>
              <img
                src={avatar}
                alt="Avatar"
                className="avatar img-fluid rounded-circle"
                style={{ width: "40px", height: "40px" }}
              />
              {isLoggedIn ? "Logout" : "Login"}
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
