const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    const response = await axios.post("http://localhost:8000/api/books/login", {
      email,
      password,
    });

    // Appeler handleLogin pour stocker l'ID utilisateur
    handleLogin(response.data.user.firstName, response.data.user.lastName, response.data.user._id);

    // Rediriger après connexion réussie
    navigate("/");
  } catch (err) {
    console.error("Erreur lors de la connexion", err);
  }
};
