import React, { useState, useContext } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { UserContext } from "../context/UserContext";  // Importation du contexte utilisateur
import "./Form.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const { handleLogin } = useContext(UserContext);  // Utilisation du contexte pour la connexion

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post(
        "http://localhost:8000/api/books/login", 
        {
          email,
          password,
        }
      );
  
      // Stocker le message de succès
      setMessage(response.data.message);
  
      // Appeler handleLogin pour mettre à jour l'état utilisateur dans le contexte
      handleLogin(response.data.user.firstName, response.data.user.lastName, response.data.user._id);  // Inclure l'ID utilisateur
  
      // Rediriger après connexion réussie
      navigate("/");
  
    } catch (err) {
      setMessage("");  // Réinitialiser le message en cas d'erreur
      setError(
        err.response && err.response.data.message
          ? err.response.data.message
          : "Erreur lors de la connexion. Veuillez vérifier vos informations."
      );
    }
  };
  

  return (
    <div className="container">
      <div className="row justify-content-center my-5">
        <div className="col-md-6">
          <h2 className="text-center book-club-title">Login</h2>

          {message && <div className="alert alert-success">{message}</div>}
          {error && <div className="alert alert-danger">{error}</div>}

          <form onSubmit={handleSubmit}>
            <div className="form-group my-4">
              <label htmlFor="email" className="form-label">Email</label>
              <input
                type="email"
                className="form-control"
                id="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group my-4">
              <label htmlFor="password" className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                id="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button type="submit" className="btn btn-primary w-100">Login</button>

            <p className="text-center mt-3">
              <Link to="/register" className="nav-link btn-link">Create an account</Link>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
