import React, { createContext, useState } from 'react';

// Crée le contexte utilisateur
export const UserContext = createContext();

// Fournit le contexte à toute l'application
export const UserProvider = ({ children }) => {
  const [user, setUser] = useState({
    firstName: localStorage.getItem('firstName') || '',
    lastName: localStorage.getItem('lastName') || '',
    userId: localStorage.getItem('userId') || '',  // Ajoute l'ID utilisateur
  });

  const handleLogin = (firstName, lastName, userId) => {
    setUser({ firstName, lastName, userId });
    localStorage.setItem('firstName', firstName);
    localStorage.setItem('lastName', lastName);
    localStorage.setItem('userId', userId);  // Stocke l'ID utilisateur
  };

  const handleLogout = () => {
    setUser({ firstName: '', lastName: '', userId: '' });
    localStorage.removeItem('firstName');
    localStorage.removeItem('lastName');
    localStorage.removeItem('userId');  // Supprime l'ID utilisateur lors du logout
  };

  return (
    <UserContext.Provider value={{ user, handleLogin, handleLogout }}>
      {children}
    </UserContext.Provider>
  );
};
