import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext";
import logo from "../assets/logo.png"; // Assurez-vous que le chemin de votre logo est correct
import loginIcon from "../assets/login-icon.png"; // Assurez-vous que le chemin de l'icône de connexion est correct
import logoutIcon from "../assets/logout-icon.png"; // Assurez-vous que le chemin de l'icône de déconnexion est correct

const NavBar = () => {
  const { user, handleLogout } = useContext(UserContext); // Récupère l'utilisateur actuel et la fonction de déconnexion
  const navigate = useNavigate();

  // Gestion du clic sur le bouton "New Book"
  const handleNewBookClick = (e) => {
    if (!user || !user.firstName) {
      e.preventDefault(); // Empêche la redirection vers la page "New Book"
      navigate("/login"); // Redirige vers la page de connexion
    }
  };

  // Gestion du clic sur le bouton de déconnexion
  const handleLogoutClick = () => {
    handleLogout(); // Appelle la fonction de déconnexion du contexte
    localStorage.clear(); // Efface le localStorage
    navigate("/"); // Redirige vers la page d'accueil après la déconnexion
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/">
          <img src={logo} alt="Book Club Logo" className="navbar-logo" />
        </Link>
      </div>
      <ul className="navbar-links">
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/books">Books</Link>
        </li>
        <li>
          <Link to="/favorites">Favorites</Link>
        </li>
        <li>
          <Link to="/new-book" onClick={handleNewBookClick}>
            New Book
          </Link>
        </li>
      </ul>
      <div className="navbar-auth">
        {user && user.firstName ? (
          <>
            <span className="navbar-user">
              {`Welcome, ${user.firstName} ${user.lastName}`}
            </span>
            <button className="logout-button" onClick={handleLogoutClick}>
              <img src={logoutIcon} alt="Logout Icon" className="auth-icon" />
              Logout
            </button>
          </>
        ) : (
          <Link to="/login">
            <button className="login-button">
              <img src={loginIcon} alt="Login Icon" className="auth-icon" />
              Login
            </button>
          </Link>
        )}
      </div>
    </nav>
  );
};

export default NavBar;
