import React, { useState } from "react"; // Importer useState
import { Link } from "react-router-dom";
import "./Navbar.css";
import logoutIcon from "../assets/logout.svg"; // Chemin vers l'icône Logout
import loginIcon from "../assets/login.png"; // Chemin vers l'icône Login
import 'bootstrap/dist/css/bootstrap.min.css';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // État pour gérer la connexion

  const handleLoginLogout = () => {
    if (isLoggedIn) {
     
      console.log("Logout");
      setIsLoggedIn(false);
    } else {
     
      console.log("Login");
      setIsLoggedIn(true); // Simulez la connexion pour cet exemple
    }
  };

  return (
    <nav className="navbar navbar-expand-lg">
      <div className="container-fluid">
        <h2 className="book-club-title">Book Club</h2>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/">Accueil</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/BookList">Book List</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/NewBooks">New Book</Link>
            </li>
          </ul>
          <div className="navbar-nav ml-auto">
            {isLoggedIn ? (
              <button className="nav-link btn btn-link" onClick={handleLoginLogout}>
                <img
                  src={logoutIcon}
                  alt="Logout"
                  className="avatar img-fluid"
                />
              </button>
            ) : (
              <Link to="/login" className="nav-link btn btn-link" onClick={handleLoginLogout}>
                <img
                  src={loginIcon}
                  alt="Login"
                  className="avatar img-fluid"
                />
              </Link>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
