import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { UserContext } from "../context/UserContext";  
import "./Form.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({}); // Gérer les erreurs par champ
  const [message, setMessage] = useState("");
  const [alreadyLoggedInMessage, setAlreadyLoggedInMessage] = useState(""); // Nouveau message d'alerte pour utilisateur déjà connecté
  const navigate = useNavigate();
  const { handleLogin, user } = useContext(UserContext); // Obtenir l'utilisateur depuis le contexte

  // Si l'utilisateur est connecté, afficher un message et rediriger
  useEffect(() => {
    if (user) {
      setAlreadyLoggedInMessage("Vous êtes déjà connecté.");
    
      setTimeout(() => {
        navigate("/"); // Rediriger vers la page d'accueil après 3 secondes
      }, 3000);
    }
  }, [user, navigate]);

  
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post(
        "http://localhost:8000/api/books/login", 
        { email, password }
      );
  
      setMessage(response.data.message);

      handleLogin(response.data.user.firstName, response.data.user.lastName, response.data.user.userId);

      navigate("/");

    } catch (err) {
      setMessage("");
      if (err.response && err.response.data.message) {
        const fieldErrors = {};

        // Vérifier et afficher les erreurs spécifiques
        if (err.response.data.message.includes("Email incorrect")) {
          fieldErrors.email = "Email incorrect";
        }
        if (err.response.data.message.includes("Utilisateur non trouvé")) {
          fieldErrors.email = "Utilisateur non trouvé";
        }
        if (err.response.data.message.includes("Mot de passe incorrect")) {
          fieldErrors.password = "Mot de passe incorrect";
        }
        if (err.response.data.message.includes("Tous les champs sont obligatoires")) {
          fieldErrors.general = "Veuillez remplir tous les champs";
        }

        setErrors(fieldErrors); // Assigner les erreurs au champ correspondant
      } else {
        setErrors({ general: "Erreur lors de la connexion. Veuillez vérifier vos informations." });
      }
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center my-5">
        <div className="col-md-6">
          <h2 className="text-center book-club-title">Login</h2>

          {/* Message si l'utilisateur est déjà connecté */}
          {alreadyLoggedInMessage && (
            <div className="alert alert-info text-center">{alreadyLoggedInMessage}</div>
          )}

          {!user && (
            <>
              {/* Message de succès */}
              {message && <div className="alert alert-success">{message}</div>}
              
              {/* Affichage des erreurs générales */}
              {errors.general && <div className="alert alert-danger">{errors.general}</div>}

              <form onSubmit={handleSubmit}>
                <div className="form-group my-4">
                  <label htmlFor="email" className="form-label">Email</label>
                  <input
                    type="text"
                    className="form-control"
                    id="email"
                    placeholder="Enter your email"
                    value={email}
                    onChange={(e) => {
                      setEmail(e.target.value);
                      setErrors({ ...errors, email: "" }); // Effacer l'erreur email lors de la modification
                    }}
                    required
                  />
                  {errors.email && <div className="text-danger">{errors.email}</div>}
                </div>

                <div className="form-group my-4">
                  <label htmlFor="password" className="form-label">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => {
                      setPassword(e.target.value);
                      setErrors({ ...errors, password: "" }); // Effacer l'erreur password lors de la modification
                    }}
                    required
                  />
                  {errors.password && <div className="text-danger">{errors.password}</div>}
                </div>

                <button type="submit" className="btn btn-primary w-100">Login</button>

                <p className="text-center mt-3">
                  <Link to="/register" className="nav-link btn-link">Create an account</Link>
                </p>
              </form>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Login;
