import React, { useState } from "react";
import "./Login.css";
const NewBook = () => {
  const [title, setTitle] = useState(""); 
  const [copies, setCopies] = useState(1); 
  const [image, setImage] = useState(null); // État pour l'image

  const handleSubmit = (e) => {
    e.preventDefault(); // Empêche le rechargement de la page

    // Ici vous pouvez gérer l'ajout du livre (par exemple, envoyer les données à une API)
    const newBook = {
      title,
      copies,
      image,
    };

    console.log("Nouveau livre ajouté :", newBook);
    
    // Réinitialiser le formulaire après soumission
    setTitle("");
    setCopies(1);
    setImage(null);
  };

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setImage(URL.createObjectURL(e.target.files[0])); // Créer une URL pour l'image sélectionnée
    }
  };

  return (
    <div className="container">
      <h2 className="text-center">Ajouter un Nouveau Livre</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Titre du Livre</label>
          <input
            type="text"
            id="title"
            className="form-control"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="copies">Nombre d'Exemplaires</label>
          <input
            type="number"
            id="copies"
            className="form-control"
            value={copies}
            onChange={(e) => setCopies(e.target.value)}
            min="1"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="image">Image du Livre</label>
          <input
            type="file"
            id="image"
            className="form-control"
            accept="image/*"
            onChange={handleImageChange}
            required
          />
        </div>

        {image && (
          <div className="mt-3">
            <h5>Aperçu de l'Image :</h5>
            <img src={image} alt="Aperçu" style={{ width: "100px", height: "auto" }} />
          </div>
        )}

        <button type="submit" className="btn btn-primary mt-3">
          Ajouter le Livre
        </button>
      </form>
    </div>
  );
};

export default NewBook;
