import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "./EditBook.css";
import"./Form.css";
const BookDetails = () => {
  const { id } = useParams(); // Récupère l'ID du livre depuis l'URL
  const [book, setBook] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/books/${id}`);
        setBook(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération du livre", error);
      }
    };

    fetchBook();
  }, [id]);

  const addToFavorites = async () => {
    try {
      await axios.post(`http://localhost:8000/api/users/add-favorite`, { bookId: id });
      alert("Livre ajouté aux favoris avec succès");
    } catch (error) {
      console.error("Erreur lors de l'ajout aux favoris", error);
    }
  };

  const deleteBook = async () => {
    try {
      await axios.delete(`http://localhost:8000/api/books/${id}`);
      alert("Livre supprimé avec succès");
      navigate("/books");  // Redirige vers la liste des livres après suppression
    } catch (error) {
      console.error("Erreur lors de la suppression du livre", error);
    }
  };
  

  const editBook = () => {
    navigate(`/edit-book/${id}`); 
  };

  if (!book) {
    return <div>Chargement...</div>;
  }
const handleChange = (e) => {
  const { name, value } = e.target;  // Récupère le nom du champ et la valeur saisie
  setBook({
    ...book,  // Garde les autres propriétés du livre inchangées
    [name]: value  // Met à jour la propriété du livre correspondante au nom du champ
  });
};

  return (
    <div className="container">
    <h2 className="book-club-title">Modifier le livre</h2>
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label className="form-label">Titre</label>
        <input
          type="text"
          name="title"
          className="form-control"
          value={book.title}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label className="form-label">Auteur</label>
        <input
          type="text"
          name="author"
          className="form-control"
          value={book.author}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label className="form-label">Description</label>
        <textarea
          name="description"
          className="form-control"
          value={book.description}
          onChange={handleChange}
          required
        ></textarea>
      </div>
      <button type="submit" className="btn btn-primary">Enregistrer les modifications</button>
    </form>
  </div>
  
  );
};

export default BookDetails;
