import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "./Form.css";
import etoile from "../assets/favori.png"; 

const BookDetails = () => {
  const { id } = useParams();  // Récupère l'ID du livre depuis l'URL
  const [book, setBook] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);  // État pour gérer le favori
  const navigate = useNavigate();
  const userId = "user-id-from-context";  // Remplacer par l'ID de l'utilisateur connecté

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8000/api/books/${id}`
        );
        setBook(response.data);

        // Vérifier si le livre est déjà un favori pour cet utilisateur
        const favoriteResponse = await axios.get(
          `http://localhost:8000/api/users/${userId}/favorites`
        );
        if (favoriteResponse.data.favorites.includes(id)) {
          setIsFavorite(true);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération du livre", error);
      }
    };

    fetchBook();
  }, [id, userId]);

  const addToFavorites = async () => {
    try {
      await axios.post(`http://localhost:8000/api/users/${userId}/add-favorite`, {
        bookId: id,
      });
      setIsFavorite(true);  // Met à jour l'état pour afficher l'étoile
      alert("Livre ajouté aux favoris avec succès");
    } catch (error) {
      console.error("Erreur lors de l'ajout aux favoris", error);
    }
  };

  const deleteBook = async () => {
    try {
      await axios.delete(`http://localhost:8000/api/books/${id}`);
      alert("Livre supprimé avec succès");
      navigate("/books");  // Redirige vers la liste des livres après suppression
    } catch (error) {
      console.error("Erreur lors de la suppression du livre", error);
    }
  };

  const editBook = () => {
    navigate(`/edit-book/${id}`);  // Redirige vers la page d'édition du livre
  };

  if (!book) {
    return <div>Chargement...</div>;
  }

  return (
    <div className="book-card">
      <div className="book-cover-container">
        <img 
          src={`http://localhost:8000${book.imageUrl}`} 
          alt={book.title} 
          className="img-book-card"
        />
        {isFavorite && (
          <img src={etoile} alt="Favori" className="favorite-star" />  // Affiche l'étoile si favori
        )}
      </div>

      <h2 className="book-title">{book.title}</h2>
      <p><span className="form-span">Auteur</span> : {book.author}</p>
      <p><span className="form-span">Description</span> : {book.description}</p>

      <div className="actions">
        <button className="btn btn-primary w-100" onClick={addToFavorites}>
          Ajouter aux favoris
        </button>
        <button className="btn btn-primary w-100" onClick={deleteBook}>
          Supprimer
        </button>
        <button className="btn btn-primary w-100" onClick={editBook}>
          Modifier
        </button>
      </div>
    </div>
  );
};

export default BookDetails;
