import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import loginIcon from "../assets/login.png";  // Icône pour login
import logoutIcon from "../assets/logout.svg";  // Icône pour logout
import "./Navbar.css"
const Navbar = ({ firstName, lastName }) => {
  const [userFirstName, setUserFirstName] = useState(firstName || "");
  const [userLastName, setUserLastName] = useState(lastName || "");
  const isLoggedIn = !!localStorage.getItem("token");

  // Utiliser useEffect pour charger les données du localStorage si non fournies
  useEffect(() => {
    if (!firstName || !lastName) {
      const storedFirstName = localStorage.getItem("firstName");
      const storedLastName = localStorage.getItem("lastName");
      if (storedFirstName && storedLastName) {
        setUserFirstName(storedFirstName);
        setUserLastName(storedLastName);
      }
    }
  }, [firstName, lastName]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("firstName");
    localStorage.removeItem("lastName");
    window.location.reload();  // Rafraîchir la page après déconnexion
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg">
        <div className="container-fluid">
          <h2 className="book-club-title">Book Club</h2>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link className="nav-link" to="/">Accueil</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/books">Book List</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/NewBook">New Book</Link>
              </li>
            </ul>
            <div className="navbar-nav ml-auto">
              {isLoggedIn ? (
                <>
                  <Link to="/" className="nav-link btn btn-link" onClick={handleLogout}>
                    <img
                      src={logoutIcon}
                      alt="Logout"
                      className="avatar img-fluid"
                    />
                  </Link>
                </>
              ) : (
                <Link to="/login" className="nav-link btn btn-link">
                  <img
                    src={loginIcon}
                    alt="Login"
                    className="avatar img-fluid"
                  />
                </Link>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Div pour afficher le message "Welcome" après le menu */}
      {isLoggedIn && userFirstName && userLastName && (
        <div className="welcome-message">
          <h3>Welcome {userFirstName} {userLastName}</h3>
        </div>
      )}
    </>
  );
};

export default Navbar;
