import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import React, { useState, useEffect } from "react";
import Login from "./components/Login";
import BookList from "./components/BookList";
import BookDetails from "./components/BookDetails";
import NavBar from "./components/NavBar";
import NewBook from "./components/NewBook";
import Register from "./components/Register";

function App() {
  // État pour gérer les informations de l'utilisateur connecté
  const [user, setUser] = useState({
    firstName: localStorage.getItem("firstName") || "",
    lastName: localStorage.getItem("lastName") || "",
  });

  // Fonction pour mettre à jour les informations de l'utilisateur après la connexion
  const handleLogin = (firstName, lastName) => {
    // Met à jour l'état local avec les informations utilisateur
    setUser({ firstName, lastName });

    // Stocke aussi ces informations dans le localStorage
    localStorage.setItem("firstName", firstName);
    localStorage.setItem("lastName", lastName);
  };

  // Utilise useEffect pour synchroniser l'état avec localStorage quand la page est chargée
  useEffect(() => {
    const storedFirstName = localStorage.getItem("firstName");
    const storedLastName = localStorage.getItem("lastName");

    if (storedFirstName && storedLastName) {
      setUser({
        firstName: storedFirstName,
        lastName: storedLastName,
      });
    }
  }, []); // Ce useEffect se déclenche uniquement une fois lors du montage du composant

  return (
    <Router>
      {/* Passer les informations utilisateur à la NavBar */}
      <NavBar firstName={user.firstName} lastName={user.lastName} />
      <Routes>
        {/* Passer handleLogin comme prop au composant Login */}
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/books" element={<BookList />} />
        <Route path="/NewBook" element={<NewBook />} />
        <Route path="/books/:id" element={<BookDetails />} />
        <Route path="/" element={<BookList />} />
      </Routes>
    </Router>
  );
}

export default App;
