import React, { useState } from "react";
import axios from "axios";
import "./Form.css";

const NewBook = () => {
  const [book, setBook] = useState({
    title: '',
    description: '',
    imageUrl: '' // Change le nom de la propriété pour stocker l'URL
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value }); // Met à jour les champs
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Vérifie que la description a au moins 5 caractères
    if (book.description.length < 5) {
      alert('La description doit contenir au moins 5 caractères.');
      return;
    }

    // Création d'un objet à envoyer
    const bookData = {
      title: book.title,
      description: book.description,
      imageUrl: book.imageUrl // Utilise l'URL de l'image
    };

    try {
      const response = await axios.post('http://localhost:8000/api/books/add-book', bookData, {
        headers: { 'Content-Type': 'application/json' },
      });
      alert('Livre ajouté avec succès');
    } catch (error) {
      console.error('Erreur lors de l\'ajout du livre', error);
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center my-5">
        <div className="col-md-6">
          <h2 className="text-center book-club-title">Ajouter un Livre</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group my-4">
              <label htmlFor="title" className="form-label">
                Titre
              </label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                value={book.title}
                onChange={handleChange}
                placeholder="Entrer le titre du livre"
                required
              />
            </div>

            <div className="form-group my-4">
              <label htmlFor="description" className="form-label">
                Description
              </label>
              <textarea
                className="form-control"
                id="description"
                name="description"
                value={book.description}
                onChange={handleChange}
                placeholder="Entrer la description"
                required
              />
            </div>

            <div className="form-group my-4">
              <label htmlFor="imageUrl" className="form-label">
                URL de l'image
              </label>
              <input
                type="text"
                className="form-control"
                id="imageUrl"
                name="imageUrl"
                value={book.imageUrl}
                onChange={handleChange}
                placeholder="Entrer l'URL de l'image"
                required
              />
            </div>

            <button type="submit" className="btn btn-primary w-100">
              Ajouter Livre
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewBook;
