import React, { useState, useRef, useContext } from "react";
import axios from "axios";
import { UserContext } from "../context/UserContext"; // Importez UserContext
import "./Form.css";

const NewBook = () => {
  const [book, setBook] = useState({
    title: "",
    author: "",
    description: "",
    image: null,
  });

  const { user } = useContext(UserContext); // Récupérez user depuis UserContext
  const [imagePreview, setImagePreview] = useState(null);
  const [error, setError] = useState(""); // État pour les messages d'erreur
  const [validationErrors, setValidationErrors] = useState([]); // Nouvel état pour stocker les erreurs de validation
  const fileInputRef = useRef(null);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      setBook({ ...book, image: files[0] });
      setImagePreview(URL.createObjectURL(files[0]));
    } else {
      setBook({ ...book, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setValidationErrors([]); // Réinitialiser les erreurs avant l'envoi

    const formData = new FormData();
    formData.append("title", book.title);
    formData.append("author", book.author);
    formData.append("description", book.description);
    formData.append("image", book.image);
    formData.append("userId", user.userId); // Ajoutez userId à formData

    try {
      const response = await axios.post(
        "http://localhost:8000/api/books/add-book",
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );
      alert("Livre ajouté avec succès");

      // Réinitialiser le formulaire après soumission
      setBook({
        title: "",
        author: "",
        description: "",
        image: null,
      });
      setImagePreview(null);
      fileInputRef.current.value = "";
    } catch (error) {
      console.error("Erreur lors de l'ajout du livre:", error.response ? error.response.data : error);
      if (error.response && error.response.data) {
        if (error.response.data.errors) {
          // Si des erreurs de validation spécifiques existent, les afficher
          setValidationErrors(error.response.data.errors);
        } else {
          // Autres erreurs
          setError(error.response.data.message);
        }
      } else {
        setError("Une erreur est survenue lors de l'ajout du livre.");
      }
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center my-5">
        <div className="col-md-6">
          <h2 className="text-center book-club-title">Ajouter un Livre</h2>

          {error && <div className="alert alert-danger">{error}</div>}
          
          {/* Affichage des erreurs de validation */}
          {validationErrors.length > 0 && (
            <div className="alert alert-danger">
              <ul>
                {validationErrors.map((err, index) => (
                  <li key={index}>{err}</li>
                ))}
              </ul>
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="form-group my-4">
              <label htmlFor="title" className="form-label">
                Titre
              </label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                value={book.title}
                onChange={handleChange}
                placeholder="Entrer le titre du livre"
                required
              />
            </div>

            <div className="form-group my-4">
              <label htmlFor="author" className="form-label">
                Auteur
              </label>
              <input
                type="text"
                className="form-control"
                id="author"
                name="author"
                value={book.author}
                onChange={handleChange}
                placeholder="Entrer le nom de l'auteur"
                required
              />
            </div>

            <div className="form-group my-4">
              <label htmlFor="description" className="form-label">
                Description
              </label>
              <textarea
                className="form-control"
                id="description"
                name="description"
                value={book.description}
                onChange={handleChange}
                placeholder="Entrer la description"
                required
              />
            </div>

            <div className="form-group my-4">
              <label htmlFor="image" className="form-label">
                Télécharger une image
              </label>
              <input
                type="file"
                className="form-control"
                id="image"
                name="image"
                ref={fileInputRef}
                onChange={handleChange}
                required
              />
            </div>

            {imagePreview && (
              <div className="my-4">
                <img src={imagePreview} alt="Aperçu" id="img-uploaded" />
              </div>
            )}

            <button type="submit" className="btn btn-primary w-100">
              Ajouter Livre
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewBook;
