import React, { useState } from "react"; // Importer useState
import { Link } from "react-router-dom";
import "./Navbar.css";
// import avatar from "../assets/profile_placeholder.png"; // Chemin vers l'image par défaut
import logoutIcon from "../assets/logout.svg"; // Chemin vers l'icône Logout
import loginIcon from "../assets/login.png"; // Chemin vers l'icône Login
import 'bootstrap/dist/css/bootstrap.min.css';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // État pour gérer la connexion

  const handleLoginLogout = () => {
    setIsLoggedIn(!isLoggedIn); // Inverser l'état de connexion
    console.log(isLoggedIn ? "Logout" : "Login");
  };

  return (
    <nav className="navbar navbar-expand-lg ">
      <div className="container-fluid">
       
          <h2 className="text-center display-4 my-4">Book CLUB</h2>
        
      
        <div className="collapse navbar-collapse justify-content-between" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/">Accueil</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/register">Register</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/books/new">New</Link>
            </li>
          </ul>
          <div className="navbar-nav ml-auto">
            <button className="nav-link btn btn-link" onClick={handleLoginLogout}>
              <img 
                src={isLoggedIn ? logoutIcon : loginIcon} // Changer l'icône selon l'état
                alt={isLoggedIn ? "Logout" : "Login"}
                className="avatar img-fluid "
                
              />
              
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
