// Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import avatar from './avatar.png'; // Assurez-vous de mettre le bon chemin vers votre image

const Navbar = () => {
    const isLoggedIn = false; // Remplacez ceci par votre logique d'authentification

    const handleLoginLogout = () => {
        // Ajoutez ici la logique pour gérer la connexion/déconnexion
        console.log(isLoggedIn ? 'Logout' : 'Login');
    };

    return (
        <nav className="navbar navbar-expand-lg ">
            <div className="container-fluid">
                <h1 className="text-center display-4 my-4">
                    Book CLUB
                </h1>
                <div className="collapse navbar-collapse justify-content-between" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <Link className="nav-link" to="/">Accueil</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/register">Register</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/books/new">New</Link>
                        </li>
                    </ul>
                    <div className="navbar-nav ml-auto">
                        <button 
                            className="nav-link btn btn-link" 
                            onClick={handleLoginLogout}
                            style={{ display: 'flex', alignItems: 'center' }}
                        >
                            <img src={avatar} alt="Avatar" style={{ width: '30px', height: '30px', marginRight: '8px' }} />
                            {isLoggedIn ? 'Logout' : 'Login'}
                        </button>
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
