import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import loginIcon from "../assets/login.png";  // Icône pour login
import logoutIcon from "../assets/logout.svg";  // Icône pour logout
import './Nav.css';
const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");

  // Charger les informations de l'utilisateur depuis localStorage
  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedFirstName = localStorage.getItem("firstName");
    const storedLastName = localStorage.getItem("lastName");

    if (token && storedFirstName && storedLastName) {
      setIsLoggedIn(true);
      setFirstName(storedFirstName);
      setLastName(storedLastName);
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  const handleLogout = () => {
    // Déconnecter l'utilisateur en supprimant les informations de localStorage
    localStorage.removeItem("token");
    localStorage.removeItem("firstName");
    localStorage.removeItem("lastName");
    setIsLoggedIn(false);
  };

  return (
    <nav className="navbar navbar-expand-lg">
      <div className="container-fluid">
        <h2 className="book-club-title">Book Club</h2>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/">Accueil</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/books">Book List</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/NewBook">New Book</Link>
            </li>
          </ul>
          <div className="navbar-nav ml-auto">
            {isLoggedIn ? (
              <>
                {/* Afficher le prénom et le nom si l'utilisateur est connecté */}
                <span className="nav-link">{firstName} {lastName}</span>
                <Link to="/" className="nav-link btn btn-link" onClick={handleLogout}>
                  <img
                    src={logoutIcon}
                    alt="Logout"
                    className="avatar img-fluid"
                  />
                </Link>
              </>
            ) : (
              <Link to="/login" className="nav-link btn btn-link">
                <img
                  src={loginIcon}
                  alt="Login"
                  className="avatar img-fluid"
                />
              </Link>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
