import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import "./Form.css"; 

const { loginUser } = require('../controllers/Login-Controller'); // Assurez-vous d'importer le contrôleur de login

// Route POST pour gérer la connexion
router.post('/login', loginUser);
const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); 
  const navigate = useNavigate(); 

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log("Tentative de connexion avec", email, password);

      const response = await axios.post("http://localhost:8000/api/login", {
        email,
        password,
      });

      console.log("Réponse de l'API:", response.data);

      // Si la connexion réussit, stocker le token et rediriger
      localStorage.setItem("token", response.data.token);
      navigate("/listbook"); // Rediriger vers la liste des livres

    } catch (err) {
      console.error("Erreur lors de la connexion:", err.response ? err.response.data : err);

      if (err.response && err.response.data.message) {
        setError(err.response.data.message);
      } else {
        setError("Erreur lors de la connexion. Veuillez vérifier vos informations.");
      }
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center my-5">
        <div className="col-md-6">
          <h2 className="text-center book-club-title">Login</h2>
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={handleSubmit}>
            <div className="form-group my-4">
              <label htmlFor="email" className="form-label">
                Email
              </label>
              <input
                type="email"
                className="form-control"
                id="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group my-4">
              <label htmlFor="password" className="form-label">
                Password
              </label>
              <input
                type="password"
                className="form-control"
                id="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button type="submit" className="btn btn-primary w-100">
              Login
            </button>

            <p className="text-center mt-3">
              <Link to="/register" className="nav-link btn-link">
                Create an account
              </Link>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
